mapper.select:
SELECT *
FROM BOARD 
WHERE board_no = #{ boardNo }

SELECT member_no
FROM IMAGE
WHERE board_no = #{ boardNo }

SELECT *
FROM FILE
WHERE board_no = #{ boardNo }

mapper.update:
UPDATE BOARD 
SET board_title = #{ boardTitle }, board_content = #{ boardContent } 
WHERE board_no = #{ boardNo }

UPDATE BOARD
SET board_deleted = '1'
WHERE board_no = #{ boardNo }

UPDATE BOARD 
SET board_count = board_count + 1
WHERE board_no = #{ boardNo }

mapper.select:
SELECT board_reco, board_nreco, board_singo
FROM BOARD 
WHERE board_no = #{ boardNo }

mapper.update:
UPDATE BOARD
SET board_reco = board_reco + 1
WHERE board_no = #{boardNo}

UPDATE BOARD
SET board_nreco = board_nreco + 1
WHERE board_no = #{boardNo}

UPDATE BOARD
SET board_singo = board_singo + 1
WHERE board_no = #{boardNo}

mapper.select:
SELECT member_no
FROM RECO
WHERE board_no = #{boardNo} AND member_no = #{memberNo}

SELECT member_no
FROM NRECO
WHERE board_no = #{boardNo} AND member_no = #{memberNo}

SELECT member_no
FROM SINGO
WHERE board_no = #{boardNo} AND member_no = #{memberNo}
